script:
start()
    .then(_ => send())
    .catch(err => {
        console.log(err)
        send(new Error(`Sending the email failed with following error: ${err.message}`))
    })

async function start () {
    const TO = await getSandboxProperty('to')
    const sendgridKey = await getSandboxProperty('SENDGRID_KEY')
    const message = await getSandboxProperty('message')
    const subject = await getSandboxProperty('subject')
    const from = await getSandboxProperty('from')

    const messageParsed = waylayUtil.template(options, message)
    const subjectParsed = waylayUtil.template(options, subject)

    if (!waylayUtil.validateEmail(TO) || !waylayUtil.validateEmail(from)) {
        const message = "Emails are not valid: to=" + TO + ", from=" + from
        throw new Error(message)
    }

    const message = {
        html: messageParsed,
        subject: subjectParsed,
        from: from,
        to: TO
    }

    const requestOptions = {
      url: 'https://api.sendgrid.com/api/mail.send.json',
      formData: message,
      'auth': {
            'bearer': sendgridKey 
        }
    }

    return requestP.post(requestOptions)
}

async function getSandboxProperty (key, defaultValue) {
    const vaultKeyStart = 'VAULT:'

    const enhancedProperty = waylayUtil.getProperty(options, key) || defaultValue || ''

    if (_.isEmpty(enhancedProperty) || !enhancedProperty.startsWith(vaultKeyStart)) return enhancedProperty

    const vaultKey = enhancedProperty.replace(vaultKeyStart, '')

    return waylay.vault.get(vaultKey)
        .then(res => {
            console.log(`Successfully got key from vault ${vaultKey} -> ${res}`)
            return res
        })
        .catch(err => {
            console.error(`Failed to get property '${enhancedProperty}' from vault`, err)
            throw err
        })
}
