async function main () {
  const resource = options.node.RESOURCE
  const { payload } = options.requiredProperties

  if (!resource) {
    throw new Error('No resource provided')
  }

  const body = parsePayload(payload)

  await waylay.data.postSeries(resource, body, { forward: false, store: true })

  send()
}

function parsePayload (payload) {
  if (!payload) {
    throw new Error('No payload provided')
  }

  try {
    payload = JSON.parse(payload)
  } catch (_) {
    console.log(payload)
    throw new Error('payload has to be a valid JSON object')
  }

  return payload
}

main().catch(send)
