script:
const resource = options.node.RESOURCE
const metadata = options.requiredProperties.metadata
let parsedMetadata

if (resource === undefined || resource === '') {
  send(new Error('resource not defined'))
}

if (!metadata) {
  send(new Error('Missing property'))
}

try {
  parsedMetadata = JSON.parse(metadata)
} catch (err) {
  send(new Error('Failed to parse to object: ' + err))
}

waylay.resources.update(resource, parsedMetadata)
  .then(() => {
    send()
  })
  .catch(err => {
    console.log('err', err.response)
    send(new Error('Failed to update resource: ' + err))
  })
