script:
var props = options.requiredProperties;
var OAuthConsumerKey = options.globalSettings.GOOGLEDRIVE_CONSUMERID;
var OAuthConsumerSecret = options.globalSettings.GOOGLEDRIVE_CONSUMERSECRET;
var clientId = options.globalSettings.DASHBOARD_KEY;
var clientSecret = options.globalSettings.DASHBOARD_SECRET;
var dashboardDomain = options.globalSettings.DASHBOARD_DOMAIN;
var worksheetName = props.worksheet || "Data";
var file = props.file;
var message = waylayUtil.template(options, props.message);
var nodename = options.node ? options.node.NAME : '';

var profile = props.profile || waylayUtil.getResource(options);
var timezone = props.timezone || 'Europe/Berlin';
var _xml2js = require('xml2js').Parser().parseString;

// variables;
var _auth;
var _xclient;
var _sheet;

// tools
var xmlSafeValue = function(val){
  if ( val == null ) return '';
  return String(val).replace(/&/g, '&amp;')
      .replace(/</g, '&lt;')
      .replace(/>/g, '&gt;')
      .replace(/"/g, '&quot;');
};
var xmlSafeColumnName = function(val){
  if (!val) return '';
  return String(val).replace(/[\s_]+/g, '')
      .toLowerCase();
};
var xml2js = function(data) {
  return new Promise(function(resolve, reject) {
    _xml2js(data, function(err, res) {
      if (err !== undefined) {
        resolve(res);
      } else {
        reject(err);
      }
    });
  });
};
function mapLink(link) {
  var elem = link['$'];
  var rels = elem.rel.split('#');
  var type = rels[rels.length - 1];
  var href = elem.href;
  return [type, href];
}

// API
var getToken = function() {
    return new Promise(function(resolve, reject) {
        var options = {
            url: "https://"+dashboardDomain+"/api/token/googledrive/" + profile,
            auth: {
                user: clientId,
                password: clientSecret
            }
        };
        try {
            request(options, function(error, response, body) {
                if (!error && response.statusCode == 200) {
                    var bodyJson = JSON.parse(body);
                    console.info(bodyJson);
                    resolve(bodyJson);
                } else {
                    reject(new Error("Calling google drive api failed: " + error + " " + body + options.url));
                }   
            });
        } catch(err){
            reject(new Error("Calling google drive api failed: " + err));
        }
    });
};
var _xmlAtom = function(accessToken, url, data, method, ifmatch) {
  if (ifmatch === undefined) {
    ifmatch = true;
  }
  console.log(method + ' -> ' + url);
  return new Promise(function(resolve, reject) {
    request({
      url: url,
      headers: {
        'Content-Type': 'application/atom+xml; charset=UTF-8',
        'Authorization': 'Bearer ' + accessToken,
       'If-Match': ifmatch ? '*' : undefined,
        'Gdata-Version': '3.0'
      },
      method: method,
      body: data
    }, function(error, response, body) {
      console.log(method + ' <- ' + url);
      if (error !== undefined) {
        resolve(response, body);
      } else {
        reject(error);
      }
    });
  });
};
function XmlAtomClient(accessToken) {
    this.post = function(url, data, ifmatch) {
        return _xmlAtom(accessToken, url, data, 'POST', ifmatch);
    }

    this.put = function(url, data, ifmatch) {
        return _xmlAtom(accessToken, url, data, 'PUT', ifmatch);
    }
}
function requestP(options) {
  var method = options.method || "GET";
  console.log(method + ' -> ' + options.url);
  return new Promise(function(resolve, reject){
    var start = new Date().getTime();
    console.log(method + '  ' + options.url);
    request(options, function(err, response, body) {
      var end = new Date().getTime();
      var status = '-';
      if(response) {
        status = response.statusCode
      }
      if(err){
        reject(err);
      }else{
        resolve({
          response: response,
          body: body
        });
      }
    });
  });
}
function findInDrive(name, type, pathid) {
  var q = "title = '" + name+"' and mimeType='" + type + "'";
  if (pathid !== undefined) {
    q += " and '" + pathid + "' in parents";
  }
  return requestP({
    url: 'https://www.googleapis.com/drive/v2/files?q=' + encodeURIComponent(q),
    method: 'GET',
    auth: _auth
  })
  .then(function(response) {
    var json = JSON.parse(response.body);
    var items = _.filter(json.items, function(item) {return !item.labels.trashed;});
    return _.map(items, function(item) {
      return {
        id: item.id,
        name: item.title,
        isParentRoot: item.parents[0] ? item.parents[0].isRoot : false
      };
    });
  });
}
function generatePromiseRoot(item, last, type) {
  return findInDrive(item, type)
    .then(function(items) {
      var file = _.find(items, function(i) { return i.isParentRoot; });
      if (file !== undefined && file !== null) {
        return file;
      } else {
        if (last) {
          return createSheet(item);
        } else {
          return createFile({
            mimeType: 'application/vnd.google-apps.folder',
            title: item,
            parents: [{ id: 'root' }]
          });
        }
      }
    });
}
function extendPromiseForFetching(promise, item, last, type) {
  return promise.then(function(file) {
    return findInDrive(item, type, file.id)
      .then(function(files) {
        var selectedFile = files[0];
        if (selectedFile !== undefined) {
          return selectedFile;
        } else {
          var parent = {
            'id': file.id
          };
          if (last) {
            parent.kind = 'drive#fileLink';
          }
          return createFile({
            mimeType: type,
            title: item,
            parents: [parent]
          });
        }
      });
  });
}
// TODO: make this code better
function createOrOpenSheet(pathname) {
  var parts = pathname.split('/');
  var index = 0;
  var promise;

  _.each(parts, function(item) {
    var last = index === parts.length - 1;
    var type = last ? 'application/vnd.google-apps.spreadsheet' : 'application/vnd.google-apps.folder';
    if (!(promise !== undefined)) {
      promise = generatePromiseRoot(item, last, type);
    } else {
      promise = extendPromiseForFetching(promise, item, last, type);
    }
    index++;
  });

  return promise;
}
function createSheet(name) {
  return createFile({
      'mimeType': 'application/vnd.google-apps.spreadsheet',
      'title': name,
    });
}
function createFile(content) {
  console.log('Creating file ' + content.title + '.');
 var url = 'https://www.googleapis.com/drive/v2/files/';

  return requestP({
    url: url,
    method: 'POST',
    auth: _auth,
    headers: [
      {
        name: 'content-type',
        value: 'application/json'
      }
    ],
    json: content
  })
    .then(function(resp) {
      return {
        title: content.name,
        id: resp.body.id
      };
    });
}
function listWorksheets(sheet) {
  return requestP({
    url: 'https://spreadsheets.google.com/feeds/worksheets/key/private/full'.replace('key', sheet.id),
    method: 'GET',
    auth: _auth
  })
    .then(function(res) {
      return xml2js(res.body);
    })
    .then(function(sheetInfo) {

      return _.map(sheetInfo['feed']['entry'], function(worksheet) {
        var d = worksheet['id'][0].split('/');
        return {
          id: d[d.length - 1],
          title: worksheet['title'][0]['_'],
          links: _.reduce(_.map(worksheet['link'], mapLink), function(o, v) {o[v[0]] = v[1]; return o;}, {})
        };
      });
    });
}
function createWorksheet(sheet, worksheetname) {
  var data = '<entry xmlns="http://www.w3.org/2005/Atom" xmlns:gs="http://schemas.google.com/spreadsheets/2006"><title>$title$</title><gs:rowCount>1000</gs:rowCount><gs:colCount>26</gs:colCount></entry>'
    .replace('$title$', xmlSafeValue(worksheetname));
  return _xclient.post('https://spreadsheets.google.com/feeds/worksheets/key/private/full'.replace('key', sheet.id), data, false)
    .then(function(response, body) {
      return xml2js(response.body);
    })
    .then(function(worksheetInfo) {

      worksheetInfo = worksheetInfo['entry'];
      var d = worksheetInfo['id'][0].split('/');
      return {
        id: d[d.length - 1],
        title: worksheetInfo['title'][0]['_'],
        links: _.reduce(_.map(worksheetInfo['link'], mapLink), function(o, v) {o[v[0]] = v[1]; return o;}, {})
      };
    });
}
function GoogleWorksheet(worksheet) {
  this.worksheet = worksheet;
  var self = this;
  var cellApi = worksheet.links.cellsfeed;
  var listApi = worksheet.links.listfeed || cellApi.replace('cells', 'list');

  this.getLists = function() {
    return requestP({
      url: listApi,
      method: 'GET',
      auth: _auth
    })
    .then(function(data) {
      return xml2js(data.body);
    })
    .then(function(js) {
      return js.feed.entry || [];
    });
  };

  this.getCells = function() {
    return requestP({
      url: cellApi,
      method: 'GET',
      auth: _auth
    })
    .then(function(data) {
      return xml2js(data.body);
    })
    .then(function(js) {
      return js.feed.entry || [];
    });
  };

  this.updateCellBatchRequestBuilder = function() {
    return new CellBatchRequestBuilder(cellApi);
  };

  this.addLine = function(line) {
   var contents = '<entry xmlns="http://www.w3.org/2005/Atom" xmlns:gsx="http://schemas.google.com/spreadsheets/2006/extended">';

   _.each(line, function(v, k) {
     var sk = xmlSafeColumnName(k);
     var sv = xmlSafeValue(v);
     contents += '<gsx:' + sk + '>' + sv + '</gsx:' + sk + '>';
   });

   contents += '</entry>';

   return _xclient.post(listApi, contents, false)
     .then(function(response, body) {
       return xml2js(response.body);
     });

  };

  var createCellName = function(x, y) {
    return 'R' + y + "C" + x;
  };

  this.setCell = function(x, y, id, content) {
    var cname = createCellName(x,y);
    var contents = '<entry xmlns="http://www.w3.org/2005/Atom" xmlns:gs="http://schemas.google.com/spreadsheets/2006">';
    contents += '<id>' + cellApi + '/' + cname + '</id>';
    contents += '<link rel="edit" type="application/atom+xml" href="' + cellApi + '/' + cname + '" />';
    contents += '<gs:cell row="' + y + '" col="' + x + '" inputValue="' + content + '"  />';
    contents += '</entry>';

    return _xclient.put(cellApi + '/' + cname, contents)
      .then(function(r) { return xml2js(r.body);})
      ;
  };
}
function CellBatchRequestBuilder(cellApi) {
  this.cellApi = cellApi;
  var self = this;
  var contents = '<feed xmlns="http://www.w3.org/2005/Atom" xmlns:batch="http://schemas.google.com/gdata/batch" xmlns:gs="http://schemas.google.com/spreadsheets/2006">';
  contents += '<id>' + cellApi + '</id>';
  var createCellName = function(x, y) {
    return 'R' + y + "C" + x;
  };

  var _cell = function(x, y, id, content, action) {
    contents += '<entry>';
    var cname = createCellName(x,y);
    contents += '<batch:id>' + id + '</batch:id>';
    contents += '<batch:operation type="' + action + '" />';
    contents += '<title type="text">' + id + '</title>';
    contents += '<id>' + cellApi + '/' + cname + '</id>';
    contents += '<link rel="edit" type="application/atom+xml" href="' + cellApi + '/' + cname + '" />';
    contents += '<gs:cell row="' + y + '" col="' + x + '" inputValue="' + content + '" />';
    contents += '</entry>';
    return self;
  };

  this.updateCell = function(x, y, id, content) {
    return _cell(x, y, id, content, 'update');
  };

  this.makeRequest = function() {
    contents += '</feed>';
    return _xclient.post(cellApi + '/batch', contents)
      .then(function(response) {
        return xml2js(response.body);
      });
  };

}

if(clientId !== undefined && clientSecret !== undefined && dashboardDomain !== undefined && file !== undefined && message !== undefined){
    getToken()
        .then(function(token) {
            _auth = {
                'bearer': token.accessToken
            };
            _xclient = new XmlAtomClient(token.accessToken);
            return createOrOpenSheet(file);
        })
        .then(function(sheet) {
            _sheet = sheet;
            return listWorksheets(sheet);
        })
        .then(function(worksheets) {
            var worksheet = _.find(worksheets, function(obj) { return obj.title === worksheetName; });

            if (worksheet) {
                return Promise.resolve(worksheet);
            } else {
                return createWorksheet(_sheet, worksheetName);
            }
        })
        .then(function(worksheet) {
            _worksheet = new GoogleWorksheet(worksheet);
            return _worksheet.getCells();
        })
        .then(function(cells) {
            if (cells.length === 0) {
                var builder = _worksheet.updateCellBatchRequestBuilder();
                var x = 0;
                var y = 1;
                builder.updateCell(++x, y, 'date', 'date');
                builder.updateCell(++x, y, 'value', 'value');
                return builder.makeRequest();
            }
            return '';
        })
        .then(function(data) {
            return _worksheet.addLine({
                date: moment().tz(timezone).format('DD/MM/YYYY HH:mm:ss'),
                value: message
            });
        })
        .then(function(d) {
            send(null, {rawData : {sheet: _sheet.id, worksheet: _worksheet.worksheet.id}});
        })
        .catch(function(error) {
            send(error);
        });
} else {
    send(new Error("Missing properties"));
}
